export const actionAdd = (id) => (
    {
      type: "ADD",
      payload: {
        id: id
      }
    }
  )
  export const actionAddFav = (id) => (
    {
      type: "ADD_TO_FAVORITE_FROM_LIST",
      payload: {
        id: id
      }
    }
  )

export const actionDelete = (id) => (
    {
      type: "DEL",
      payload: {
        id: id
      }
    }
  )

  const getFilmData = async (s) => {
    if (s === 0) {
      throw 'Invalid id';
    }
  
    const response = await fetch(`http://www.omdbapi.com/?s=${s}&apikey=b045d90e`);
    const data = await response.json();
  
    return data;
  }
  
