import React, { Component } from 'react';
import './Favorites.css';
import { actionDelete } from '../../redux/actions';

import {connect} from "react-redux";


const mapState = (store) => ( {movies: store.favoriteMovies} );
const mapDispatch = (dispatch) => ({
  removeFromCard: (id) => dispatch( actionDelete(id) )
})


const connector = connect(mapState, mapDispatch);

class Favorites extends Component {
    state = {
        title: "",
        textLink: '#',
        inputActive: true       
    }
    handleChangeName = (event) => {
        this.setState({ title: event.target.value });
      };

      handleSaveList = () => {
        this.setState({
          inputActive: false,
          linkActive: true
        });
        this.saveMovies();
      };
    
    
      saveMovies = () => {
        fetch("https://acb-api.algoritmika.org/api/movies/list",
          {
            method: 'POST',
            headers: {
              'Content-type': 'application/json',
            },
            body: JSON.stringify({
              "title": this.state.title,
              "movies": this.props.favoriteMovies.map(e => e.imdbID)
            })
          })
          .then(res => res.json())
          .then(data => {
            this.setState({textLink: data.id})
          })
      };
    


    render() { 
        return (
            <div className="favorites">
                <input value={this.state.title} placeholder='Введите название списка' className="favorites__name" 
                disabled={this.state.inputActive ? null : "disabled"} onChange={this.handleChangeName} />
                <ul className="favorites__list">
                    {this.props.movies.map((item) => {
                        return <li key={item.imdbID}>{item.Title} ({item.Year}) <button onClick={()=>{this.props.removeFromCard(item.imdbID)}}> X </button> </li>;
                    })}  
                </ul>
                <button type="button" onClick={() => this.handleSaveList()}
                disabled={!this.state.title || this.props.favoriteMovies.length === 0} className="favorites__save" >Сохранить список</button>
            </div>
        );
    }
}
 
const NewFavorites =connector(Favorites)
export default NewFavorites;
