const initialStore = {
    movies: [
        {
            imdbID: 'tt3896198',
            title: "Guardians of the Galaxy Vol. 2",
            year: 2017,
            poster: "https://m.media-amazon.com/images/M/MV5BNjM0NTc0NzItM2FlYS00YzEwLWE0YmUtNTA2ZWIzODc2OTgxXkEyXkFqcGdeQXVyNTgwNzIyNzg@._V1_SX300.jpg"

        },
        {
            imdbID: 'tt0068646',
            title: "The Godfather",
            year: 1972,
            poster: "https://m.media-amazon.com/images/M/MV5BM2MyNjYxNmUtYTAwNi00MTYxLWJmNWYtYzZlODY3ZTk3OTFlXkEyXkFqcGdeQXVyNzkwMjQ5NzM@._V1_SX300.jpg"

        }
    ],

    favoriteMovies: [    ]
}

function reducer(store = initialStore, action) {
    switch (action.type) {
      case "ADD":
          const item = store.movies.find(e => e.id === action.payload.id);
          if (item) {
            const new_cart = store.favoriteMovies.slice();
            new_cart.push(item);
            return {...store, fvavoriteMovies: new_cart}
          }
        break;
      case "DEL":
          const new_cart = store.cart.filter(e => 
            e.id !== action.payload.id
          );
          return {...store, cart: new_cart}
        break;
      default:
        // console.error('unknown action');
    }
    return store;
  }

  export default reducer;
