import React, { Component } from 'react';
import MovieItem from '../MovieItem/MovieItem';
import './Movies.css';


import {actionAdd} from "../../redux/actions.js";
import {connect} from "react-redux";

const mapState = (store) => ( {movies: store.movies} );
const mapDispatch = (dispatch) => ({
  addToCard: (id) => dispatch( actionAdd(id) )
})

const connector = connect(mapState, mapDispatch);

class Movies extends Component {
    state = { 
        movies: []
    }

    // componentDidMount() {
    //     const state = store.getState();
    //     this.setState({movies: state.movies});
    //   }
    render() { 
        return ( 
            <ul className="movies">
                {this.state.movies.map((movie) => (
                    <li className="movies__item" key={movie.imdbID}>
                        <MovieItem {...movie} clicker={this.props.addToCard} />
                    </li>
                ))}
            </ul>
        );
    }
}
const NewMovies = connector(Movies);

export default Movies;
