//     console.log(error)
//     return []
//   }

// }
const getFilmData = async (s) => {
    if (s === 0) {
      throw 'Invalid id';
    }
  
    const response = await fetch(`http://www.omdbapi.com/?s=${s}&apikey=b045d90e`);
    const data = await response.json();
  
    return data;
  }
  
  getFilmData().then().catch()

class SearchBox extends Component {
    state = {
        searchLine: '' ,
        data: []
    }
    searchLineChangeHandler = (e) => {
        this.setState({ searchLine: e.target.value });
    }
    searchBoxSubmitHandler = (e) => {
        e.preventDefault();
        getFilmData(this.state.searchLine) .then(data => this.setState({data: data})).catch(error => alert(error)) 
    }
    render() {   
        const { searchLine } = this.state;
        console.log(this.state.data);

        return (
            <div className="search-box">
                <form className="search-box__form" onSubmit={this.searchBoxSubmitHandler}>
                    <label className="search-box__form-label">
                        Искать фильм по названию:
                        <input
                            value={searchLine}
                            type="text"
                            className="search-box__form-input"
                            placeholder="Например, Shawshank Redemption"
                            onChange={this.searchLineChangeHandler}
                        />
                    </label>
                    <button
                        type="submit"
                        className="search-box__form-submit"
                        disabled={!searchLine}
                        
                    >
                        Искать
                    </button>
                </form>
            </div>
        );
     }
}
 
export default SearchBox;
