import React, { Component } from 'react';
import './Favorites.css';
// import {  useSelector } from 'react-redux';
// import { useDispatch } from 'react-redux';
// import MovieItem from '../MovieItem/MovieItem';
import { actionDelete } from '../../redux/actions';

import {connect} from "react-redux";


// const mapState = (store) => ( {movies: store.favoriteMovies} );
// const mapDispatch = (dispatch) => ({
//   removeFromCard: (imdbID) => dispatch( actionDelete(imdbID) )
// })
// const dispatch = useDispatch();
    
//     const removeFromCard = (id) => {
//         dispatch(actionDelete(id))
//     }

const mapDispatchToProps = (dispatch) => {
    return {
      removeFromList: (id) => dispatch(actionDelete(id)),
    };
  };

const connector = connect(mapState, mapDispatch);

class Favorites extends Component {
    state = {
        title: 'Новый список'        
    }
    handleChangeName = (event) => {
        this.setState({ title: event.target.value });
      };


    render() { 
        return (
            <div className="favorites">
                <input placeholder='Введите название списка' className="favorites__name"  onChange={this.handleChangeName} />
                <ul className="favorites__list">
                    {this.props.movies.map((item) => {
                        return <li key={item.imdbID}>{item.Title} ({item.Year}) <button onClick={()=>{this.props.removeFromList(item.imdbID)}}> X </button> </li>;
                    })}  
                </ul>
                <button type="button" className="favorites__save" >Сохранить список</button>
            </div>
        );
    }
}
 
const NewFavorites =connector(mapStateToProps, mapDispatchToProps)(Favorites)
export default NewFavorites;
