export const actionAdd = (imdbID) => (
    {
      type: "ADD_MOVIE_TO_THE_LIST",
      payload: {
        imdbID: imdbID
      }
    }
  )

  export const actionDelete = (imdbID) => (
    {
      type: "DELETE_MOVIE_FROM_THE_LIST",
      payload: {
        imdbID: imdbID
      }
    }
  )

  export const searchMovies = (movies) => (
    {
      type: "SEARCH_MOVIES",
      payload: {
        movies: movies
      }
    }
  )
