                        return (
                            <li key={item.imdbID}>
                                <a href={"https://www.imdb.com/title/"+ item.imdbID } target="_blank">{item.Title} ({item.Year})</a>
                            </li>
                        );
                    })}
                </ul>
            </div>
        );
    }
}
 
export default connector(ListPage);
