const initialStore = {
    favoriteMovies: [ ],
    movies: [ ],
}

function reducer(store = initialStore, action) {
    switch (action.type) {
      case "ADD":
        if(store.favoriteMovies.find(item=>
          item.imdbID ===action.payload.imdbID ) ) 
          break;
         const temp=store.movies.find(item=>
          item.imdbID ===action.payload.imdbID);
          if(temp){
          const new_cart=store.favoriteMovies.slice();
          new_cart.push(temp);
          return{...store,favoriteMovies:new_cart};
      }
      break;
      case "DEL":
          const del_cart = store.favoriteMovies.filter(item => 
            item.imdbID !== action.payload.imdbID
          );
          return {...store, favoriteMovies: del_cart}
        break;

        case "SEARCH_MOVIES":
          return {...store, movies: action.payload.movies}
        break;
      default:
        // console.error('unknown action');
    }
    return store;  
  }

  export default reducer;
