const initialStore = {
    movies: [
        
    ],

    favoriteMovies: [    ]
}

function reducer(store = initialStore, action) {
    switch (action.type) {
      case "ADD":
          const item = store.movies.find(e => e.id === action.payload.id);
          if (item) {
            const new_cart = store.favoriteMovies.slice();
            new_cart.push(item);
            return {...store, favoriteMovies: new_cart}
          }
        break;
      case "DEL":
          const new_cart = store.cart.filter(e => 
            e.id !== action.payload.id
          );
          return {...store, cart: new_cart}
        break;
      default:
        // console.error('unknown action');
    }
    return store;
  }

  export default reducer;
